import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import styles from '../styles/signup.module.css';

function Signup() {
    const [user, setUser] = useState({ name: '', email: '', location: '', password: '' });
    const [error, setError] = useState('');
    const navigate = useNavigate();
    const [citySuggestions, setCitySuggestions] = useState([]);
    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        try {
            const response = await axios.post('/api/auth/signup', user);
            console.log('Server Response',response.data);
            if (response.data.success) {
                navigate('/login');
            } else {
                setError('Signup failed. Please try again.');
            }
        } catch (error) {
            console.error('Error during signup:', error);
            setError('An error occurred during signup. Please try again.');
        }
    };
    const getLocation = () => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(async (position) => {
                try {
                    const { latitude, longitude } = position.coords;
                    const response = await axios.get(
                        `https://geocode.maps.co/reverse?lat=${latitude}&lon=${longitude}&api_key=677ce00b164d2678637601fvtd84c83`
                    );
                    console.log('Location Response', response.data);
                    const city = response.data.address.city;
                    setUser({...user, location: city});
                } catch (error) {
                    setError('Could not fetch location automatically. Please enter manually.');
                }
            }, () => {
                setError('Please enable location access or enter your city manually.');
            });
        }
    };
    const handleCitySearch = async (searchTerm) => {
        if (searchTerm.length > 2) {
            try {
                const response = await axios.get(
                    `https://geocode.maps.co/search?q=${searchTerm}&api_key=677ce00b164d2678637601fvtd84c83`
                );
                console.log('Search Response:', response.data); // Add this to debug
                const cities = response.data
                    .filter(item => item.type === "city" || item.type === "administrative")
                    .map(item => item.display_name.split(',')[0].trim())
                    .filter((city, index, self) => self.indexOf(city) === index)
                    .slice(0, 5);
                console.log('Filtered Cities:', cities);
                setCitySuggestions(cities);
            } catch (error) {
                console.error('Error fetching cities:', error);
            }
        } else {
            setCitySuggestions([]);
        }
    };

    const selectCity = (city, e) => {
        e.preventDefault(); // Prevent form submission
        e.stopPropagation(); // Stop event bubbling
        setUser({...user, location: city});
        setCitySuggestions([]);
    };

    return (
        <div className={styles.container}>
            <h2 className={styles.title}>Sign Up</h2>
            {error && <p className={styles.error}>{error}</p>}
            <form onSubmit={handleSubmit} className={styles.form}>
                <input
                    className={styles.input}
                    type="text"
                    placeholder="Name"
                    onChange={(e) => setUser({...user, name: e.target.value})}
                    required
                />
                <input
                    className={styles.input}
                    type="email"
                    placeholder="Email"
                    onChange={(e) => setUser({...user, email: e.target.value})}
                    required
                />
                <div className={styles.locationContainer}>
                    <input
                        className={styles.input}
                        type="text"
                        placeholder="Enter the city name or please give location access"
                        value={user.location}
                        onChange={(e) => {
                            setUser({...user, location: e.target.value});
                            handleCitySearch(e.target.value);
                        }}                        required
                    />
                    {citySuggestions.length > 0 && (
                        <ul className={styles.suggestions}>
                            {citySuggestions.map((city, index) => (
                                <li
                                    key={index}
                                    onClick={(e) => selectCity(city, e)}
                                    className={styles.suggestionItem}
                                >
                                    {city}
                                </li>
                            ))}
                        </ul>
                    )}
                    <button
                        type="button"
                        className={styles.locationButton}
                        onClick={getLocation}
                    >
                        📍
                    </button>
                </div>
                <input
                    className={styles.input}
                    type="password"
                    placeholder="Password"
                    onChange={(e) => setUser({...user, password: e.target.value})}
                    required
                />
                <button className={styles.button} type="submit">Sign Up</button>
            </form>
        </div>
    );
}

export default Signup;
