import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import '../styles/missingpersondetails.module.css';
function MissingPersonDetails() {
  const [person, setPerson] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { id } = useParams();

  useEffect(() => {
    const fetchPerson = async () => {
      try {
        const response = await axios.get(`/api/missing-persons/{id}`);
        setPerson(response.data);
        console.log("The response is" ,response.data);
        setLoading(false);

      } catch (error) {
          console.error('Error fetching missing person details:', error);
          setError('Failed to load missing person details. Please try again.');
          setLoading(false);      }
    };
    fetchPerson();
  }, [id]);

    if (loading) return <div className="loading">Loading...</div>;
    if (error) return <div className="error">{error}</div>;
    if (!person) return <div className="not-found">Missing person not found.</div>;

    return (
      <div >
        <h1>{person.name}</h1>
      <img src={`data:image/jpeg;base64,${person.imageData}`}  alt={person.name} style={{width: '300px', height: '300px'}} />
        <p>Reported by: {person.reportedBy.Name}</p>
        <p>Contact: {person.reportedBy.Email}</p>
      </div>
  );
}

export default MissingPersonDetails;
